#PROC POUR CATEGORIE
CREATE PROCEDURE categorie_joueur()
BEGIN
	
	DECLARE dateNaiss DATETIME;
	
	SELECT date_naissance INTO dateNaiss
	FROM Joueur;
	
	CASE 
		WHEN year(dateNaiss) <= (SELECT year(NOW())-40) #né il y a plus de 40ans
		THEN UPDATE Joueur SET categorie_id = 8;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-20) and  year(dateNaiss) > (SELECT year(NOW())-40) #né il y a plus de 20ans mais moins de 40ans
		THEN UPDATE Joueur SET categorie_id = 7;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-18) and  year(dateNaiss) > (SELECT year(NOW())-20) #né il y a plus de 18ans mais moins de 20ans
		THEN UPDATE Joueur SET categorie_id = 6;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-16) and  year(dateNaiss) > (SELECT year(NOW())-18) #né il y a plus de 16ans mais moins de 18ans
		THEN UPDATE Joueur SET categorie_id = 5;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-14) and  year(dateNaiss) > (SELECT year(NOW())-16) #né il y a plus de 14ans mais moins de 16ans
		THEN UPDATE Joueur SET categorie_id = 4;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-12) and  year(dateNaiss) > (SELECT year(NOW())-14) #né il y a plus de 12ans mais moins de 14ans
		THEN UPDATE Joueur SET categorie_id = 3;
		
		WHEN year(dateNaiss) <= (SELECT year(NOW())-10) and  year(dateNaiss) > (SELECT year(NOW())-12) #né il y a plus de 10ans mais moins de 12ans
		THEN UPDATE Joueur SET categorie_id = 2;
		
		WHEN year(dateNaiss) > (SELECT year(NOW())-10) #né il y a moins de 10ans
		THEN UPDATE Joueur SET categorie_id = 1;
		
		ELSE
			BEGIN
			END;

	END CASE;
END//

